node.doctext:
[%
my $realuid = $$USER{ 'node_id' };

unless ( $realuid == 470183) {
#    return '';
}

local * listdir = sub {
    my ( $direc, $stoprecurse ) = @_;
    my $DIR;
    my @names;
    my $str = '';

    $stoprecurse ||= 0;

    $direc =~ s/\/\/+/\//g;

    opendir(DIR, $direc) || die "can't opendir $direc: $!";
    @names = grep { /[^\.]/ && -d "$direc/$_" } readdir( DIR );
    closedir DIR;

    my $nameonly = $direc;
    # No path for leaves
    $nameonly =~ s/^.*\/([^\/]+)$/$1/ if ( $stoprecurse );

    $str .= "<dl>\n";
    $str .= "<dt>";
    $str .= linkNode( $$NODE{node_id}, $nameonly, 
                      { 'listdir' => $direc } );
    $str .= "</dt>\n";
    $str .= "<dd>\n";
    for my $name ( @names ) {
        $str .= listdir( "$direc/$name", 1 ) unless ( $stoprecurse );
    }
    $str .= "</dd>\n";
    $str .= "</dl>\n";

    return $str;
};

my $listdir = $query->param( 'listdir' ) || '/';
return listdir( $listdir, 0 );

%]
