node.doctext:
[%

  return 'Do you really want to see the eldritch horrors that result when I try to write code?' unless $USER->{title} eq 'mauler';

  my $str = 'Everything\'s \"Special\" Writeups (visible only to staff members):';

  #my $csr = $dbh->prepare('SELECT node_id FROM node WHERE type_nodetype=117 AND reputation>18 ORDER BY createtime LIMIT 300');

  #my $csr = $dbh->prepare('SELECT node_id FROM node WHERE type_nodetype=117 AND reputation>18 ORDER BY reputation LIMIT 300');

  my $csr = $dbh->prepare('SELECT node_id FROM node WHERE type_nodetype=117 AND reputation>18 ORDER BY createtime DESC LIMIT 300');

  #my $csr = $dbh->prepare('SELECT writeup_id FROM writeup ORDER BY cooled DESC LIMIT 100');

  $csr->execute();

  $str .= '<br><br><table><tr bgcolor="#CCCCCC"><td width="200">Writeup</td><td width="200">Author</td></tr>';

  while(my $row = $csr->fetchrow_hashref())
  {

      my $bestnode = getNodeById($$row{node_id});

      next unless($bestnode);

      my $bestparent = getNodeById($$bestnode{parent_e2node});
      my $bestuser = getNodeById($$bestnode{author_user});

     $str.='<tr><td>'.linkNode($bestnode, $$bestnode{title}).' - '.linkNode($bestparent, 'full').' <b>'.$$bestnode{reputation}.'</b></td><td> by '.linkNode($bestuser, $$bestuser{title}).'</td></tr>';

  }

  $str .= '</table>';

  return $str;
%]
