node.doctext:
[%
my $dbh = $DB->getDatabaseHandle();
return 'No database handle!' unless $dbh;
my $rows;
my $str;
my $dbrow;
my $queryText;
my $limit = 100;

$queryText = 'SELECT user_id,stars FROM user ORDER BY stars DESC LIMIT '.$limit;
$rows = $dbh->prepare($queryText)
   or return $rows->errstr;
$rows->execute()
   or return $rows->errstr;
my $row;
$str .= '<h3>'.$limit.' Most Starred Noders</h3>';
$str .= '<ol>';
while($row = $rows->fetchrow_arrayref)
{
   $str .= '<li>'.linkNode($$row[0]).' ('.$$row[1].' stars)</li>';
}
$str .= '</ol>';
%]
