<h2>Page display</h2>
[%

my @headeroptions = ( 'audio', 'length' , 'hits' , 'dtcreate' ) ;
my @footeroptions = ( 'kill' , 'sendmsg' , 'addto' , 'social' ) ;

my $legacycheck = '^$|c:type,c:(author|pseudoanon)(,\w:' . join( ')?(,\w:' , @headeroptions ) . ',?)?' ;
my $legacyhead = '<p>'.$query->checkbox(-name=>'replaceoldheader',
		           -label=>'Overwrite all existing header settings. (Changing settings here
				   will not overwrite any custom formatting you already have in place unless
				   you check this.)')."</p>\n"
		unless $$VARS{ wuhead } =~ /^$legacycheck$/ || $query -> param( 'replaceoldheader' ) ;

$legacycheck = '^$|(l:kill)?,?c:vote,c:cfull(,\w:' . join( ')?(,\w:' , @footeroptions ) . ',?)?' ;
my $legacyfoot = '<p>'.$query->checkbox(-name=>'replaceoldfooter',
		           -label=>'Overwrite all existing footer settings. (Changing settings here
				   will not overwrite any custom formatting you already have in place unless
				   you check this.)')."</p>\n"
		unless $$VARS{ wufoot } =~ /^$legacycheck$/ || $query -> param( 'replaceoldfooter' ) ;

if(defined($query->param('change_stuff'))) {

	$$VARS{ wuhead } = 'c:type,c:author,c:audio,c:length,c:hits,r:dtcreate' unless $legacyhead ;

	$$VARS{ wuhead } =~ s/,$// ;
	foreach ( @headeroptions ) {
		if($query->param('wuhead_'.$_)) {
			$$VARS{ wuhead } .= ",c:$_," unless $$VARS{ wuhead } =~ /\w:$_/ ;
		} else {
			$$VARS{ wuhead } =~ s/,?\w:$_//g ;
		}
	}

	$$VARS{ wufoot }='l:kill,c:vote,c:cfull,c:sendmsg,c:addto,r:social' unless $legacyfoot ;
	$$VARS{ wufoot } =~ s/,$// ;
	foreach ( @footeroptions ) {
		if($query->param('wufoot_'.$_)) {
			$$VARS{ wufoot } .= ",c:$_" unless $$VARS{ wufoot } =~ /\w:$_/ ;
		} else {
			$$VARS{ wufoot } =~ s/,?\w:$_//g ;
		}
	}

	if ( $query -> param( 'nokillpopup' ) ) {
		$$VARS{ nokillpopup } = 4 ;
	} else {
		delete $$VARS{ nokillpopup }
	}
}

my $str.="<fieldset><legend>Writeup Headers</legend>\n"
	.
	htmlcode('varcheckboxinverse', 'info_authorsince_off',
			'Show how long ago the author was here')."<br>\n"
	.
	$query->checkbox(-name=>'wuhead_audio',
			   -checked=>( ($$VARS{'wuhead'}=~'audio') ? 1 : 0 ) ,
		           -label=>'Show links to any audio files')."<br>\n"
	.
	$query->checkbox(-name=>'wuhead_length',
			   -checked=>( ($$VARS{'wuhead'}=~'length') ? 1 : 0 ) ,
		           -label=>'Show approximate word count of writeup')."<br>\n"
	.
	$query->checkbox(-name=>'wuhead_hits',
			   -checked=>( ($$VARS{'wuhead'}=~'hits' || $$VARS{'wuhead'} eq '' ) ? 1 : 0 ) ,
		           -label=>'Show a hit counter for each writeup')."<br>\n"
	.
	$query->checkbox(-name=>'wuhead_dtcreate',
			   -checked=>( ($$VARS{'wuhead'}=~'dtcreate' || $$VARS{'wuhead'} eq '' ) ? 1 : 0 ) ,
		           -label=>'Show time of creation')."<br>\n"
	.
	"$legacyhead
	</fieldset>" ;

$str.="<fieldset><legend>Writeup Footers</legend>\n";

if ($$USER{title} =~ /^(?:mauler|riverrun|Wiccanpiper|DonJaime)$/ and isGod($USER)){
	# only gods can disable pop-up: they get the missing tools in Master Control
	# as of 2011-07-15 only three gods are using it. Let's lose it gradually...
	$str .= $query->checkbox(-name=>'nokillpopup',
			-checked=>( $$VARS{ nokillpopup } == 4 ) ,
			-label=> 'Admin tools always visible, no pop-up' )
		.'<br>';
}

$str.=$query->checkbox(-name=>'wufoot_sendmsg',
			   -checked=>( ($$VARS{'wufoot'}=~'sendmsg' || $$VARS{'wufoot'} eq '' ) ? 1 : 0 ) ,
		           -label=>'Show a box to send messages to the author')."<br>\n"
	.
	$query->checkbox(-name=>'wufoot_addto',
			   -checked=>( ($$VARS{'wufoot'}=~'addto' || $$VARS{'wufoot'} eq '' ) ? 1 : 0 ) ,
		           -label=>'Show a tool to add the writeup to your bookmarks,
				   		a usergroup page or a category')."<br>\n"
	.
	$query->checkbox(-name=>'wufoot_social',
			   -checked=>( ($$VARS{'wufoot'}=~'social' || $$VARS{'wufoot'} eq '' ) ? 1 : 0 ) ,
		           -label=>'Show social bookmarking buttons')."<br>\n";
$str .= "<small>To see social bookmarking buttons on other people's writeups you must enable them for yours<br>\n".
	htmlcode('varcheckboxinverse','nosocialbookmarking','Enable social bookmarking buttons on my writeups')."</small><br>\n"
		if $$VARS{nosocialbookmarking} ;

$str .= "$legacyfoot
	</fieldset>";


return $str.$query->hidden(-name=>'change_stuff');
%]
