Theme:[%
return if ($APP->isGuest($USER));

if($query->param('chosentheme')) {
	my $theme = getNodeById($query->param('chosentheme'));
	my $theme_type = $$theme{type}{title} if $theme;
	if ($theme_type eq 'theme' || $theme_type eq 'themesetting') {
		my $theme_id = $$theme{node_id};
		$$VARS{preferred_theme} = $theme_id;
		getTheme();
	}
}
delete $$VARS{preferred_theme} if $$VARS{preferred_theme} == $Everything::CONF->{system}->{default_theme} ;
htmlcode('typeMenu','chosentheme',$$VARS{preferred_theme}||$Everything::CONF->{system}->{default_theme},'theme','themesetting');
