[%
my $param_row = sub {
    # $display_name     Name shown to user
    # $param_name       Name of form field
    # $user_field       Name of user setting in $$VARS
    # $theme_field      Name of field in $$THEME
    # $noswatch         Don't show a swatch link.
    # $maxlength         Max value length
    my ( $set, $display_name, $param_name, $user_field, $theme_field, $noswatch, $maxlength ) = @_;

    $maxlength ||= 18;

    my $val = $query->param( $param_name ) || $$VARS{ $user_field } || $$THEME{ $theme_field };
    my $str = '';

    $val =~ s/[^a-zA-Z0-9,#%\- \:\;]//g;

    if ( $set ) {
        $$VARS{ $user_field } = $val;
    }

    $query->param( $param_name, $val );

    $str .= "<tr valign=\"top\">\n<td align=\"right\">" . $display_name . "</td>\n";
    $str .= "<td>" . $query->textfield( $param_name, $val, 12, $maxlength );
    if ( $theme_field ) {
        unless ( $noswatch ) {
            $str .= " (<a alt=\"Show " . $display_name . " color swatch\" ";
            $str .= "href=\"javascript:pop_small( '/index.pl?node=color_swatch&bgcolor=' + escape( ";
            $str .= "document.forms.ekwprefs." . $param_name . ".value ), 'color_swatch' );\">swatch</a>)\n";
        }
        $str .= "</td>\n";
        $str .= "<td>" . $$THEME{ $theme_field };
        $str .= " (<a href=\"javascript:set_ekw_field( document.forms.ekwprefs." . $param_name . ", ";
        $str .= "'" . $$THEME{ $theme_field } . "' );\">restore</a>)</td>\n";
    } else {
        $str .= "<td>&nbsp;</td>";
    }
    $str .= "</tr>\n\n";

    return $str;
};

my $str;

my $set = ( $query->param( 'set_values' ) eq 'true' );

# Fields
$str .= &$param_row( $set, 'Background Color',     'bgcolor',    'ekw_bgcolor',    'bodyBgcolor', 0, 96 );
$str .= &$param_row( $set, 'Odd Row Color<br>(New Writeups, user search, user nodes)<br>',
                     'oddrowcolor', 'ekw_oddrowcolor', 'oddRowColor' );
$str .= &$param_row( $set, 'Text Color',           'textcolor',  'ekw_textcolor',  'bodyTextcolor' );
$str .= &$param_row( $set, 'Unvisited Link Color', 'linkcolor',  'ekw_linkcolor',  'bodyLinkcolor' );
$str .= &$param_row( $set, 'Visited Link Color',   'vlinkcolor', 'ekw_vlinkcolor', 'bodyVlinkcolor' );
$str .= &$param_row( $set, 'Active Link Color',    'alinkcolor', 'ekw_alinkcolor', 'bodyAlinkcolor' );

# What IS this, and who added it?! -- wharf
#$str .= &$param_row( $set, 'Doc Background Color <br>(not working yet, be patient)',
#                     'doccolor', 'ekw_doccolor', 'docColor' );

$str .= "<tr><td colspan=\"3\">&nbsp;</td></tr>\n";

$str .= &$param_row( $set, 'Logo Border',      'logoborder',     'ekw_logoborder',     'logoBorder' );
$str .= &$param_row( $set, 'Logo/Nodelet Title Background',  'logobackground', 'ekw_logobackground', 'logoBackground' );
$str .= &$param_row( $set, 'Logo/Nodelet Title Text',   'logomaintext',   'ekw_logomaintext',   'logoMainText' );
$str .= &$param_row( $set, 'Logo Accent Text', 'logoaccenttext', 'ekw_logoaccenttext', 'logoAccentText' );

$str .= "<tr><td colspan=\"3\">&nbsp;</td></tr>\n";

$str .= &$param_row( $set, 'Text Font',             'textfont',     'ekw_textfont',     'textFont', 1, 48 );
$str .= &$param_row( $set, 'Text Font Size',        'textfontsize', 'ekw_textfontsize', 'textFontSize', 1 );
$str .= &$param_row( $set, '<tt>pre/tt</tt> Font',        'monofont', 'ekw_monofont', 'monoFont', 1, 48 );
$str .= &$param_row( $set, '<tt>pre/tt</tt> Font Size',        'monofontsize', 'ekw_monofontsize', 'monoFontSize', 1 );
$str .= &$param_row( $set, 'Heading Font',          'headingfont',  'ekw_headingfont',  'headingFont', 1, 48 );
$str .= &$param_row( $set, 'Logo Font',             'logofont',     'ekw_logofont',     'logoFont', 1, 48 );
$str .= &$param_row( $set, 'Logo Font Size',        'logofontsize', 'ekw_logofontsize', 'logoFontSize', 1 );

