node.doctext:
[%

return "Roll Call of the Valiant coming soon ...";

use JSON;

my $entryFee = 10;
my $initialHealth = 200;

my $villainsGroup = getNode("villains","usergroup");
my $heroesGroup = getNode("heroes","usergroup");

my $str;

if (!$$VARS{thewar}) {
$$VARS{thewar} = '{}';
}

my $warVars = from_json($$VARS{thewar});
if ($warVars->{fallen}) {
removeFromNodegroup($villainsGroup,$USER,-1);
removeFromNodegroup($heroesGroup,$USER,-1);
	updateNode($villainsGroup, -1);
	updateNode($heroesGroup, -1);

}

if ($query->param("switch")) {


if ($query->param("switch") eq 'heroes')  {
	removeFromNodegroup($villainsGroup,$USER,-1);
	insertIntoNodegroup($heroesGroup, -1, $USER);
	updateNode($villainsGroup, -1);
	updateNode($heroesGroup, -1);
	adjustExp($USER,-1*$entryFee);
	return "Thou hast regained thy light!";
}

if ($query->param("switch") eq 'villains')  {
	removeFromNodegroup($heroesGroup,,$USER,-1);
	insertIntoNodegroup($villainsGroup, -1, $USER);
	updateNode($heroesGroup, -1);
	updateNode($villainsGroup, -1);
	adjustExp($USER,-1*$entryFee);
	return "Thou hast plunged into darkness!";
}

$str .= "Fool! Thou are tempting the fates ...";


}

if (htmlcode('isInUsergroup',$$USER{user_id}.',heroes')) {
my $warVars = from_json($$VARS{thewar});
my $inventoryStr = '<p><b>Inventory</b></p>';
for my $key ( keys %{$warVars->{inventory} } ) {
   		$inventoryStr .= "<p>$key<br />".$warVars->{inventory}->{$key}->{description}."</p>";
}
return "Thou hast chosen light. For a [".$entryFee." XP|small fee], you may cast aside your honor and switch. Is this what you wish?"."<h2>".linkNode($NODE,"SWITCH",{switch => "villains"})."</h2>".$inventoryStr;
}

if (htmlcode('isInUsergroup',$$USER{user_id}.',villains')) {
my $warVars = from_json($$VARS{thewar});
my $inventoryStr = '<p><b>Inventory</b></p>';
for my $key ( keys %{$warVars->{inventory} } ) {
   		$inventoryStr .= "<p>$key<br />".$warVars->{inventory}->{$key}->{description}."</p>";
}
return "Thou hast chosen darkness.  For a [".$entryFee." XP|small fee], you may cast aside your honor and switch. Is this what you wish?"."<h2>".linkNode($NODE,"SWITCH",{switch => "heroes"})."</h2>".$inventoryStr;
}

my @artifacts = (
{ "jessicapierce" =>
	{
	"damageBonus" => 3 ,
	"description" => "A piercing barb"
	}
},

{ "asamoth" =>
	{
	"shieldBonus" => 9 ,
	"description" => "A blank and impenetrable shield"
	}
},

{ "jet-poop" =>
	{
	"damageBonus" => 12 ,
	"description" => "A knapsack of destruction"
	}
},

{ "mcflurry" =>
	{
	"shieldBonus" => 7 ,
	"description" => "A McDonald's food-like product"
	}
},

);



if ($query->param("chooseside")) {

my $artifactCount = @artifacts - 1;
my $randArtifact = $artifacts[int(rand($artifactCount))];



if ($query->param("chooseside") eq 'heroes')  {
my $usergroup = getNode("heroes","usergroup");
insertIntoNodegroup($usergroup, -1, $USER);
updateNode($usergroup, -1);
adjustExp($USER,-1*$entryFee);
my $scalar = from_json($$VARS{thewar});
$scalar->{health} = $initialHealth;
$scalar->{fallen} = 0;
for my $key ( keys %{$randArtifact} ) {
   		$scalar->{inventory}->{$key} = $randArtifact->{$key};
}
$$VARS{thewar} = to_json($scalar);
updateVars($USER,$VARS);
return "Thou hast chosen light and paid [".$entryFee."|thy penance]. Prepare yourself for [Ragnarok!|glorious battle!]";
}

if ($query->param("chooseside") eq 'villains')  {
my $usergroup = getNode("villains","usergroup");
insertIntoNodegroup($usergroup, -1, $USER);
updateNode($usergroup, -1);
adjustExp($USER,-1*$entryFee);
my $scalar = from_json($$VARS{thewar});
$scalar->{health} = $initialHealth;
$scalar->{fallen} = 0;
for my $key ( keys %{$randArtifact} ) {
   		$scalar->{inventory}->{$key} = $randArtifact->{$key};
}
$$VARS{thewar} = to_json($scalar);
setVars($USER,$VARS);
return "Thou hast chosen darkness and [".$entryFee." XP|sold thy soul]. [Ragnarok!|Go forth] and do thy master's bidding!";
}

$str .= "Fool! Thou hast chosen poorly.";

}

$str.="<h1>CHOOSE WISELY</h1>";

$str .= "<h2>".linkNode($NODE,"HERO",{chooseside => "heroes"})."</h2>";
$str .= "<h2>".linkNode($NODE,"VILLAIN",{chooseside => "villains"})."</h2>";


return $str;


%]
