my $queryText;
my $str;

my ($sec,$min,$hour,$day,$month,$yr19,@rest) =   localtime(time);
$str .= 'Web Server DateTime: '.($yr19+1900).'-'.($month+1).'-'.$day.' '.$hour.':'.$min.':'.$sec.'<br />';

my $queryText = "SELECT NOW()";
$rows = $DB->{dbh}->prepare($queryText)
  or return $rows->errstr;
$rows->execute()
  or return $rows->errstr;
while($dbrow = $rows->fetchrow_arrayref)
{
   $str .= 'DB DateTime: '.$$dbrow[0].'<br />';
}

return $str;
%]

