use Digest::MD5('md5_hex');

my $rows;
my $dbrow;
my $u;

my $queryText = "SELECT user_id FROM user ORDER BY lasttime DESC LIMIT ".$count;
$rows = $DB->{dbh}->prepare($queryText)
  or return $rows->errstr;
$rows->execute()
  or return $rows->errstr;
while($dbrow = $rows->fetchrow_arrayref)
{
  $u = getNodeById($$dbrow[0]);
  $str .= '<div class="ImgBox"><img src="http://gravatar.com/avatar/'.md5_hex($$u{email}).'?d='.$gtype.'&s='.$size.'" alt="." /><br />'.linkNode($u).'</div>';
}
$str .= '<hr style="clear:both" />';
$str .= '<input type="hidden" name="node_id" value="$$NODE{node_id}" />';
