  while(my $row = $csr->fetchrow_arrayref())
  {
    my $node = getNodeById($row->[0]);
    unless($node)
    {
       next;
    }
    if($node->{modperlsafe} != 1)
    {  
       $number++;
       $str.="<li>".linkNode($node)
    }
  }
  return "Un-audited htmlcodes: $number<br />".$str;
