[%
  return unless $USER->{title} eq "jaybonci";
  my $str = "";
  my $htmlcode = getType('htmlcode');
  my $csr = $DB->sqlSelectMany("htmlcode_id","htmlcode","modperlsafe=0");
  while(my $row = $csr->fetchrow_arrayref())
  {
    my $node = getNodeById($row->[0]);
    unless($node)
    {
       $str.="<li>Bad data in htmlcode table: ".$row->[0];
       next;
    }
    if($node->{type_nodetype} == $htmlcode->{type_nodetype})
    {
       $str.="<li>".linkNode($node)
    }
  }
  return $str;
%]
