node.doctext:
[%
return "";
use Time::Local;

my $str = "";

my $dbh = $DB->getDatabaseHandle();
my $c = $dbh->prepare("select node_id from heaven where killa_user=329 and type_nodetype=117 and author_user!=329");
$c->execute();

while(my ($id) = $c->fetchrow())
{
  my $gt = 0;
  my $tot = 1;  
  my $noinsert = 0;

  my $CHILD = getNodeById($id, 'force');
  my $PARENT = getNodeById($$CHILD{parent_e2node}, 'force');

  $str .= "For child $$CHILD{title} ($$CHILD{node_id}, of parent $$PARENT{title} ($$PARENT{node_id}) ... <br>\n"; 

  my $child_date = $$CHILD{createtime};
  my ($cyyyy, $cmm, $cdd, $chh, $cMM, $css) = ($child_date =~ /(\d+)-(\d+)-(\d+) (\d+):(\d+):(\d+)/);
  my $child_seconds = 0 ;# timelocal($css, $cMM, $chh, $cdd, $cmm - 1, $cyyyy - 1900);
  if ($cmm - 1 < 0) { $str .= "DANGER!!!<BR>" } ;

  foreach (@{ $PARENT->{group} }) 
  { 

    $noinsert = 1 if ($_ == $$CHILD{node_id});
    my $date = getNodeById($_)->{createtime};

    my ($yyyy, $mm, $dd, $hh, $MM, $ss) = ($date =~ /(\d+)-(\d+)-(\d+) (\d+):(\d+):(\d+)/);

    my $epoch_seconds = timelocal($ss, $MM, $hh, $dd, $mm - 1, $yyyy - 1900);

    if ($epoch_seconds < $child_seconds)
    {
      #$str .= "$$CHILD{node_id} was written after $_ <br>";
      $gt++;
    }
    else
    {
      #$str .= "$$CHILD{node_id} was written before $_ <br>";
    }
      $tot++;
  }


  insertIntoNodegroup($PARENT, $USER, $CHILD, $gt+1) unless ($noinsert == 1);
  $str .= "SKIP" if ($noinsert == 1);
  $str .= "Insert me after $gt ($tot)<br>";

}

$str;
%]
