node.doctext:
[%
return "Nothing to see here";
my $str = "";

my @USRS = getNodeWhere({}, getType('user'), 'node_id');

foreach (@USRS)
{
  my $U = $_;
  my $V = getVars($U);


  $str .= "For user $$U{title}:<br>";
  my $links = $DB->getDatabaseHandle()->prepare("select to_node from links where linktype = 419565 and from_node = $$U{user_id}");
  $links->execute();

  while (my ($target) = $links->fetchrow())
  {
    my $T = getNodeById($target);
    $str .= "Link to $$T{title} ($$T{node_id})<br>";


    next unless($target =~ /([0-9]*)/);
    next if(index($$V{bookbucket}, $1) > -1);

    $$V{bookbucket} .= "," if($$V{bookbucket} && $$V{bookbucket} ne "");
    $$V{bookbucket} .= $1;

  }

  setVars($U, $V);
  updateNode($U, $U, 1); 
  $str .= "bookbucket: $$V{bookbucket} <br>";


  #A test
  my $VA = getVars($U);
  $str .= "testing: $$VA{bookbucket} <br>";
}


$str;

%]
