[%
return;
# Made by Oolong, with code adapted from [A Year Ago Today] and [Reputation Graph].

my $yearsago=1;
my ($sec,$min,$hour,$mday,$month,$year,$wday,$yday,$isdst) =
        gmtime(time);
$year+= 1900;
my $strYear;


my $str='<table><tr><th>Month to</th><th colspan="2">New writeups (surviving, not hidden)</th><th colspan="2">New users</th><th colspan="2">C!s spent</th><th title="ratio of cools to new writeups">C!:NW</th><th title="New writeups per day">NW/day</th></tr>';

for ($yearsago=0; $yearsago<11; $yearsago++) {
  my $strYear = ($year+1).'';
  if (length($strYear)==1)
  {
    $strYear = "0".$strYear;
  }
  my $strDate = $year."-12-01";
  my $limit = 'type_nodetype='.getId(getType('writeup'))." and createtime >= '".$strDate."' and createtime < DATE_ADD('".$strDate."',INTERVAL 1 YEAR) and writeup.notnew=0";

  my $wucnt = $DB->sqlSelect('count(*)', 'node JOIN writeup on writeup.writeup_id=node.node_id', $limit);

  my $limit = 'type_nodetype='.getId(getType('user'))." and createtime >= '".$strDate."' and createtime < DATE_ADD('".$strDate."',INTERVAL 1 YEAR)";

  my $usercnt = $DB->sqlSelect('count(*)', 'node', $limit);

  my $limit = "tstamp >= '".$strDate."' and tstamp < DATE_ADD('".$strDate."',INTERVAL 1 YEAR)";

  my $coolcnt = $DB->sqlSelect('count(*)', 'coolwriteups', $limit);


  $str.="<tr>";
  $str .= '<td class="DateLabel">';
    if ($year==0)
    {
      $str .= '<b>'.($year+1).'</b>';
    }
    else
    {
      $str .= ($year+1);
    }
    $str .= '</td>';

  $str.= "<td>$wucnt</td><td><span class='bar' style='padding-right:".($wucnt/600)."px;'>&nbsp;</span></td><td>$usercnt</td><td><span class='bar' style='padding-right:".($usercnt/600)."px;'>&nbsp;</span></td><td>$coolcnt</td><td><span class='bar' style='padding-right:".($coolcnt/600)."px;'>&nbsp;</span></td>";
  $str.="<td>".sprintf("%.2f", $coolcnt/$wucnt)."</td>" if ($coolcnt>0);
  $str.="<td>".sprintf("%.2f", $wucnt/365.24)."</td>";
#  $str.="<td>".$coolcnt/$wucnt."</td>" if ($coolcnt>0);
  $str.="</tr>";

  $year--;
  if($year<0) {
      $year = 11;
      $year--;
    } 

}
$str.="</table>";
return $str;

%]




