node.doctext:
[%


my $csr=$DB->sqlSelectMany("*", "recording", "1", "ORDER BY recording_id DESC LIMIT 100");

return "There are no recording links yet." unless $csr->rows;
my $str="<ul>";

my $GROUP = getNode('podpeople','usergroup');
my $id = getId($USER);


while (my $recording = $csr->fetchrow_hashref) {

my $writeup=getNode($$recording{recording_of});
$str.="<li><h4><a href='$$recording{link}'>$$writeup{title}</a></h4><p>Written by ".linkNode($$writeup{author_user})." and read by ".linkNode($$recording{read_by})."</p>";

$str.="<p>".linkNode($$recording{recording_id},'recording node')."</p>" if grep /^$id$/, @{ $$GROUP{group} };

$str.="</li>";

}

$str.="</ul>";


return $str unless grep /^$id$/, @{ $$GROUP{group} };

$str .= '
    <hr />
    <b>Create a new recording:</b><br />
    <form method="post">
    <input type="hidden" name="type" value="recording">
    <input type="text" size="50" maxlength="64" name="node">
    <input type="hidden" name="op" value="new">
    <input type="hidden" name="displaytype" value="edit">
    <input type="submit" value="create">
    </form>
';

return $str;

%]
