node.doctext:
[%
# how long do blacklisted IPs block account creation? (SQL INTERVAL)
my $ipTrauma = '3 MONTH';
# for how long will a signup form still work after being served?
my $formLife = 86400;

my $invalidName = '^\W+$|[\[\]\<\>\&\{\}\|\/\\\]| .*_|_.* |\s\s|^\s|\s$';
my $validNameDescription = 'Valid user names contain at least one letter or number, and none of
		&#91; &#93; &lt; &gt; &amp; { } | / or \\. They may contain either spaces or underscores
		but not both, may not contain multiple spaces in a row and may not start or end with a space.';

# for how many days should an account activation link be valid?
my $validForDays = 10;

# with which nodetypes may usernames not clash?
my @nameBlockers = (
	'user', 'usergroup' , 'nodetype', 'fullpage'
	, 'document', 'superdoc', 'superdocnolinks', 'restricted_superdoc'
);

my $prompt = '';
my %names = ();

# use automation-resistant field names
my $seed = "$1" if join(',', $query->param) =~ /,q5q(\d+)/;
my $hashName = sub{
	my $x = crypt("$_ majtki", "\$5\$$seed}");
	$x =~ s/[^0-9A-z]/q/g;
	$x;
};
foreach ('username', 'email', 'pass', 'spambot') { $names{$_} = &$hashName };

my $username = $query -> param($names{'username'});
my $email = $query -> param($names{'email'});
my $pass = $query -> param($names{'pass'});
my $spambot = $seed ? $query -> param($names{'spambot'}) : 1;

my $formPage = sub{
	my $obfuscate = sub{
		my $x = shift;
		$x =~ s/([aeiounp])/'&#'.ord($1).';'/eg;
		$x;
	};

	$seed = time + $formLife;
	foreach ('username', 'email', 'pass', 'spambot') {$names{$_} = &$hashName}

	return htmlcode('openform')
		.$query -> fieldset({style => 'width: 32em; max-width: 100%; margin: 3em auto 0'},
			$query -> legend('Sign up')
			.$query -> p($prompt.':')
			.$query -> p({style => 'text-align: right'},
				# use silly field names and mess up labels to confuse bots
				$query -> label(&$obfuscate('Username:')
					.$query -> textfield($names{'username'}, $username, 30, 20))
				.'<br>'
				.$query -> label(&$obfuscate('Password:')
					.$query -> password_field($names{'pass'}, '', 30, 240))
				.'<br>'
				.$query -> label(&$obfuscate('Confirm password:')
					.$query -> password_field('toad', '', 30, 240))
				.'<br>'
				.$query -> label(&$obfuscate('Email address:')
					.$query -> textfield($names{'email'}, $email, 30, 240))
				.'<br>'
				.$query -> label(&$obfuscate('Confirm email:')
					.$query -> textfield('celery', '', 30, 240))
				.'<br>'
				.$query -> checkbox(
					-name => $names{'spambot'}
					, value => 1
					, checked => $spambot
					, label => 'I am an evil robot spammer')
				.'<br>'
				.$query -> submit('beseech', 'Submit')
			)
		)
	.<<END
	</form>

	<h4>Email Privacy Policy</h4>

	<p>We will only use your email to send you an account activation email and for any other email services
	that you specifically request. It will not be disclosed to anyone else.</p>

	<h4>Spam Policy</h4>

	<p>We neither perpetrate nor tolerate spam.
	</p>

	<p>New accounts advertizing any product, service or web site (including "personal" sites and blogs)
	in their posts or in their profile are subject to immediate deletion. Their details may be submitted to public blacklists
	for the use of other web sites.
	</p>

	<h4>Underage users</h4>

	<p>Everything2 may include member-created content designed for an adult audience. Viewing this content does not require
	an account. For logged-in account holders, Everything2 may display text conversations conducted by adults and intended for
	an adult audience. On-site communications are not censored or restricted by default. Users under the age of 18 are advised
	that they should expect to be interacting primarily with adults and that the site may not be considered appropriate by their
	parents, guardians, or other powers-that-be. Everything2 is not intended for use by children under the age of 13 and does not
	include any features or content designed to appeal to children of that age.
	</p>
END
};

my $nameTaken = sub{
	my $name = shift;
	my $x;
	map { return $x if $x = getNode($name, $_); } @nameBlockers;
	return 0 unless $name =~ /( |_)/;

	my $other = $1 eq ' ' ? '_' : ' ';
	$name =~ s/[ _]/$other/g;
	map { return $x if $x = getNode($name, $_); } @nameBlockers;
	return 0;
};

my @addrs = $APP->getIp();
my $logUser = getNode('Virgil', 'user');

my $secLog = sub{
	my $msg = shift;
	$APP -> securityLog($NODE, $logUser, $msg.'; IP: '.join('-', @addrs));
};

#######

# filter out undesirables

if ($username && $email && $pass){
	my @undesirable = ();

	# check for blacklisted IP
	my $blacklisted;
	foreach (@addrs){
		last if $blacklisted = $DB->sqlSelect(
			'ipblacklist_ipaddress'
			, 'ipblacklist'
			, "ipblacklist_ipaddress = '$_'
			AND ipblacklist_timestamp > DATE_SUB(NOW(), INTERVAL $ipTrauma)"
		);
	}

	push @undesirable, "request from blacklisted IP: $blacklisted" if $blacklisted;

	# check if email address belongs to a locked account
	my $lockedUser = $DB->sqlSelect(
		"user_id"
		, "user"
		, "email = " . $DB->quote($email) . " AND acctlock != ''"
	);

	push @undesirable, 'same email address as locked user: '.linkNode($lockedUser) if $lockedUser;

	push @undesirable, "email address $email is from spamland domain $1" if $email =~ /\.(ammuca\.eu)$/;

	# check for locked user cookie infection
	push @undesirable, 'infected by cookie from locked user' if htmlcode('checkInfected');

	# break things and annoy the user if necessary
	if (@undesirable){
		$logUser = getNode('Klaproth', 'user');
		my $log = join('; ', @undesirable);
		&$secLog("Sign up rejected: $log");

		$query -> delete(values %names);
		$prompt = int(rand(4)) + 1;
	}
}

#######

# ask them nicely...
if (time > $seed || !$pass || !$username || !$email || $prompt == 1){
	$prompt = 'Please fill in all fields';

# then check they've jumped through the hoops:
}elsif($pass ne $query -> param('toad') || $prompt == 2){
	$prompt = "Passwords don't match";

}elsif($email ne $query -> param('celery') || $prompt == 3){
	$prompt = "Email addresses don't match";

# RFC 5231 & 5232 are not regexp friendly. Only validate host part:
}elsif($email !~ /.+@[\w\d.-]+\.[\w]+$/){
	$prompt = "Please enter a valid email address";

}elsif($username =~ /$invalidName/){
	$prompt = "$validNameDescription<br>Please enter a valid user name";

}elsif(my $old = &$nameTaken($username) || $prompt == 4){
	&$secLog("Rejected username $username: matches ".linkNode($old)) unless $prompt;
	$prompt = 'Sorry, that username is already taken. Please try a different one';

}elsif($spambot){
	$prompt = 'We regret that only human beings or entities claiming
		human status are permitted to sign up for an account.
		Please indicate your humanity';
	my $value = ' (parameter value "'.encodeHTML($spambot).'")' unless $spambot eq '1';
	&$secLog("User failed human intelligence test$value");
}

$query -> delete('toad');

return &$formPage if $prompt;

#######
# all tests passed: create account

# construct new user
my ($pwhash, $salt) = $APP -> saltNewPassword($pass);

my $user = {
	nick => $username
	, email => $email
	, salt => $salt
};

# get activation link
my $params = $APP -> getTokenLinkParameters($user, $pass, 'activate', time() + $validForDays * 86400);
my $link = urlGen($params, 'no quotes', getNode('Confirm password', 'superdoc'));

# save token & expiry time in case we want to resend link later, and don't let user log on yet
$user -> {passwd} = $$params{token}.'|'.$$params{expiry};

# create user
$user = $DB -> insertNode($username, 'user', -1, $user);

unless ($user){
	&$secLog('Failed to create new user: username '.encodeHTML($username, 1));
	return $query -> p('Sorry, something just went horribly wrong. Your account has
		not been created. Please try again.');
}

getRef $user;
$user -> {author_user} = $user -> {node_id};
$DB -> updateNode($user, -1);

&$secLog('Created user '.linkNode($user));

# log ip addresses
map {
	$DB->sqlInsert(
		"iplog"
		, {iplog_user => $$user{user_id}
		, iplog_ipaddy => $_}
	)
} @addrs;

### Save a few initial settings
my $uservars = getVars($user);
$$uservars{'showmessages_replylink'} = 1;
$$uservars{ipaddy} = join ',', @addrs;
$$uservars{preference_last_update_time} = 1;
$$uservars{coolsafety} = 1;
setVars($user, $uservars);

# send activation email
my %mail = %{getNode('Welcome to Everything2', 'mail')};

$mail{doctext} =~ s/«name»/$username/;
$mail{doctext} =~ s/«link»/$link/g;
$mail{doctext} =~ s/«servername»/$ENV{SERVER_NAME}/g;

$APP -> node2mail($email, \%mail, 1);

# tell them we're wonderful
return "<h3>Welcome to Everything2, $username!
	</h3>
	<p>Your new user account has been created, and an email has been sent to the address you provided.
	You cannot use your account until you have followed the link in the email to activate it.
	This link will expire in $validForDays days.
	</p>
	<p>The email contains some useful information, so please read it carefully, print it
	out on high-quality paper, and hang it on your wall in a tasteful frame.
	</p>";
#%]
