node.doctext:
[%

my $owners = getVars(getNode('usergroup owners','setting'));
my @grps;
my %protected = map { getId(getNode($_,'usergroup')) => 1} ();

my @insiders = keys %$owners;
if(isGod($USER)){
    @insiders = ();
    my $csr = $DB->sqlSelectMany('node_id', 'node', 'type_nodetype='.getId(getType('usergroup')));
    my $row;
    push @insiders, $$row{node_id} while($row = $csr->fetchrow_hashref());
}

foreach(sort {$a <=> $b} @insiders)
{
  unless($protected{$_})
  {
    if($$owners{$_} == $$USER{node_id} or isGod($USER))
    {
     push @grps, $_;
    }
  }
}

return 'You have nothing to edit here.' if @grps == 0;

my $str.='<table><tr><td width="200" valign="top" border="1">Choose a usergroup to edit:<ul>
';

foreach(@grps)
{
   my $n = getNodeById($_);
   $str.='<li>Edit '.linkNode($NODE, "$$n{title}", { for_usergroup => $$n{node_id}}).'</li>
';
}
$str.='
</ul>';

return $str.'</td></tr></table>' unless $query->param('for_usergroup');
my $usergroup = getNodeById($query->param('for_usergroup'));
return $str.'</td></tr></table>' unless $usergroup;

$str.='</td><td valign="top">';
my $foo = $owners;
return $str unless($$foo{$$usergroup{node_id}} == $$USER{node_id} or isGod($USER));

$str.='<big><big>Editing '.linkNode($usergroup).'</big></big><br />';

foreach($query->param) {
	if($_ =~ /rem_(\d+)/) {
		my $u = getNodeById($1);
		next unless $u;
		removeFromNodegroup($usergroup,$u, -1);
		$str.='Removed: '.linkNode($u).'<br>';
	}
}

if($query->param('addperson')) {
	my $u;
	foreach(split("\n",$query->param('addperson'))) {
		$_ =~ s/\s+$//g;
		if(defined( $u=getNode($_,'user')) ) {
			insertIntoNodegroup($usergroup, -1, $u);
			$str.='Added user: '.linkNode($u).'<br>';
			next;
		}
		if(defined( $u=getNode($_,'usergroup')) ) {
			insertIntoNodegroup($usergroup, -1, $u);
			$str.='Added usergroup: '.linkNode($u).'<br>';
			next;
		}
		$str .= '<font color="red">No such user&#91;group&#93; ' . $_ . '!</font><br>';
	}
}

updateNode($usergroup, -1);

$str.=htmlcode('openform');
$str.='<table>
<tr><td width="200"><strong>Remove?</strong></td><td width="300">User</td></tr>
';
foreach(@{$$usergroup{group}})
{
 my $u = getNodeById($_);
 next unless $u;
# Added timesince // Apatrix 2008-06-25
# $str.="<tr><td><input type=\"checkbox\" name=\"rem_$$u{node_id}\"></td><td>[" . $$u{title} . ']</td></tr>';
# $str.="<tr><td><input type=\"checkbox\" name=\"rem_$$u{node_id}\"></td><td>[" . $$u{title} . ']';
 $str.="<tr><td><input type=\"checkbox\" name=\"rem_$$u{node_id}\"></td><td>" . linkNode($u);
 $str.=' <small>('.htmlcode('timesince',($u->{lasttime}).',1').')</small>';
 $str.='</td></tr>';
}

$str.="</table>";

$str.="Add people (one per line):<br>";
$str.="<textarea name=\"addperson\" rows=\"20\" cols=\"30\"></textarea>";
$str.="<input type=\"submit\" name=\"submit\" value=\"Update group\">";
$str.="<input type=\"hidden\" name=\"for_usergroup\" value=\"$$usergroup{node_id}\">";

$str.="</form>";

my $isOwner = $$owners{$$usergroup{node_id}} == $$USER{node_id};

if(isGod($USER) or $isOwner){
	$str.="<p><b>Users Ignoring This Group</b> (includes ex-members)</p>";
	$str.="<ul>";
	my $ignore = $DB->sqlSelectMany('messageignore_id', 'messageignore', 'ignore_node='.$query->param('for_usergroup'));
	my $ignorelist;
	while($ignorelist = $ignore->fetchrow_hashref()) {
		$str.="<li>".linkNode($$ignorelist{messageignore_id})."</li>";
	}
	$str.="</ul>";
}


$str.="</td></tr></table>";
return $str;

%]
