node.doctext:
[%
  return "um, no" if $USER->{node_id} == $HTMLVARS{'guest_user'};

  $VARS->{squawk_rooms} ||= "0";
  $VARS->{squawk_talk} ||= "0";
  $VARS->{squawk_talk} = $query->param("talkin") if($query->param("talkin") or $query->param("talkin") == "0");

  my $oldroom = $USER->{in_room};
  $USER->{in_room} = $VARS->{squawk_talk};
  my $str;

  #here's where the magic happens
  my $MSGOP = getNode("message", "opcode");
  eval($MSGOP->{code}) if($query->param("squawk"));
  $query->param("message", "");
  $str.="$@" if $@ and isGod($USER);

  $USER->{in_room} = $oldroom;

  my $add_room = getNodeById($query->param("add_room"));
  $add_room = {"node_id" => "0", "type_nodetype" => getId(getType("room"))} if $query->param("add_room") == "0";

  if($add_room and $add_room->{type_nodetype} = getId(getType("room")))
  {
      $add_room->{criteria} ||= 1;
      $VARS->{squawk_rooms} .= ",".getId($add_room) if($add_room->{criteria} and eval($add_room->{criteria}));
  }

 my @rooms;

  if($query->param("showaddrooms")){
    my $csr = $DB->sqlSelectMany("node_id", "node", "type_nodetype=".getId(getType("room")));

    while(my $row = $csr->fetchrow_hashref)
    {
       my $N = getNodeById($row->{node_id});
       push @rooms, $N if $N;
    }

    @rooms = sort {lc($a->{title}) cmp lc($b->{title}) } @rooms;
    unshift @rooms, {node_id => "0", title => "outside"};

    $str.= htmlcode("openform");
    $str.="<select name=\"add_room\">";
    foreach(@rooms)
    {
       $str.="<option value=\"$_->{node_id}\">$_->{title}</option>";
    }
    $str.="</select>";
    $str.="<input type=\"submit\" value=\"Add Room\"></form>";
    $str.="<br><br>";
  }else{
    $str.=htmlcode("openform");
    $str.="<input type=\"hidden\" name=\"showaddrooms\" value=\"1\">";
    $str.="<input type=\"submit\" value=\"Add Rooms\">";
    $str.="</form>";
  }

  my $sqrms;
  foreach(split(",",$VARS->{squawk_rooms}))
  {
    $sqrms->{$_} = 1;
  }

  foreach(my @params = $query->param)
  {
     next unless $_ =~ /^unsquawk_(\d+)$/;
     delete $sqrms->{$1};
  }

  $VARS->{squawk_rooms} = join ",", keys %$sqrms;
  return $str unless (keys %$sqrms > 0);

  #my $TAGNODE = getNode 'approved html tags', 'setting';
  #my $TAGS=getVars($TAGNODE);

  $str.=htmlcode("openform");
  $str.="<table>";
  $str.="<tr>";
  $str.="<td width=\"100\"><strong>Room</strong></td>";
  $str.="<td width=\"150\"><strong>Remove Watch</strong></td>";
  $str.="<td width=\"150\"><strong>Talk</strong></td>";
  foreach(keys %$sqrms)
  {
      my $room = getNodeById($_);
      $room = {title => "outside", node_id => "0", type_nodetype => getId(getType("room")) } if($_ == 0);
      next unless $room;
      $str.="<tr>";
      $str.="<td>".(($_ == 0)?("outside"):(linkNode($room)))."</td>";
      $str.="<td><input type=\"checkbox\" name=\"unsquawk_$room->{node_id}\"></td>";
      $str.="<td><input type=\"radio\" value=\"$room->{node_id}\" name=\"talkin\"".(($VARS->{squawk_talk} == $room->{node_id})?(" CHECKED "):(""))."></td>";
      $str.="</tr>";
  }
  $str.="</table>";
  $str.="<br>";
  $str.="<p align=\"center\"><hr width=\"200\"></p>"; 

  my $RT = getVars(getNode("room topics", "setting"));
  $VARS->{squawk_talk} ||= 0;
  $str.="<p align=\"center\"><table><tr><td>".$RT->{$VARS->{squawk_talk}}."</td></tr></table></p>";

  $str.="<br>";
  $str.="<table>";
  $str.="<tr>";
  #unless($$NODE{title} eq "chatterlight"){
    $str.="<td width=\"75\"><strong>Where</strong></td>";
    $str.="<td width=\"50\"><strong>When</strong></td>";
    $str.="<td width=\"100\"><strong>Who</strong></td>";
    $str.="<td width=\"100\"><strong>What</strong></td>";
  #}
  $str.="</tr>";

##########
# Build ignore list and then get the messages

  my $ignorelist = $DB->sqlSelectMany('ignore_node', 'messageignore', 'messageignore_id='.$$USER{user_id});
  my @ignorelistlist;
  while (my ($ignorelistu) = $ignorelist->fetchrow) { push @ignorelistlist, $ignorelistu; }
  my $ignoreStr = join(", ",@ignorelistlist);
  my $searchcriteria = "(".join(" or ", map {" room='$_' " } keys %$sqrms).") and for_user=0 and unix_timestamp(now())-unix_timestamp(tstamp) < 300";
  $searchcriteria .= " and author_user not in ($ignoreStr)" if $ignoreStr;
  $searchcriteria .= " order by tstamp asc";

#  my $msgcsr = $DB->sqlSelectMany("*", "message", "(".join(" or ", map {" room='$_' " } keys %$sqrms).") and for_user=0 and unix_timestamp(now())-unix_timestamp(tstamp) < 300 order by tstamp asc");

  my $msgcsr = $DB->sqlSelectMany("*", "message", $searchcriteria);

while(my $msg = $msgcsr->fetchrow_hashref)
  {
      my $room = getNodeById($msg->{room});
      $room = {title => "outside", node_id => "0", type_nodetype => getId(getType("room")) } if($msg->{room} == 0);
      next unless $room;

      my $user = getNodeById($msg->{author_user});
      next unless $user;

      my $msgtext = encodeHTML($msg->{msgtext});
      if ($msgtext =~ /^\/me (.*)$/){
      $msgtext = "<em>$1</em>";
      }

      #if ($NODE->{title} eq "chatterlight")
      #{
      #   $msgtext = "<small>$msgtext</small>";
      #}

      $str.="<tr>";
      $str.="<td>".(($msg->{room} == $VARS->{squawk_talk})?("<strong>$room->{title}</strong>"):("($room->{title})"))."</td>";
      $str.="<td><small>".htmlcode('parsetimestamp', "$msg->{tstamp},2")."</small></td>";
      $str.="<td>&lt;".linkNode($user)."&gt;</td>";
      $str.="<td>$msgtext</td>";
      $str.="</tr>";
  }
  $str.="</table>";
  $str.="<br><br>";
  $str.="<input type=\"text\" width=\"400\" name=\"message\" maxlength=\"255\">";
  $str.="<input type=\"hidden\" name=\"squawk\" value=\"1\">";
  $str.="<input type=\"submit\" value=\"talk\">";
  $str.="</form>";
  return $str;
%]
