node.doctext:
[%
return "[Swap] at work. If you're a coder type, click on viewcode to see what I'm up to." unless $$USER{title} eq 'Swap';

my $doit = $query->param("doit");

my $str = linkNode($NODE,
                   "Click here to do it. Only click once!", 
                   {doit => 1});


if ($doit) {

  my $swappian_id = 1394568;

  $str ="<p>the maintenance nodes with writeups in them are </p>";
  my $table .= "<table><tr><td>key</td><td>Maintenance Node</td></tr>\n";
  my $talktome .= "<table>
                   <tr><td>Author</td>
                   <td>Node</td>
                   <td>Total WU count</td></tr>\n";

  foreach my $key ( keys %HTMLVARS) {
    my $val = $HTMLVARS{$key};

    next unless $val =~ /^\d*$/;
    my $node = getNodeById($val);
    next unless ($$node{'type'} -> {'title'}) eq 'e2node';
    next unless $$node{'group'};

    $table .= "<tr> <td> $key </td> <td>".
      linkNode($HTMLVARS{$key})."</td> </tr>\n";

    my @wu_ids = @{$$node{'group'}};

    #Don't count the first writeup, which explains what the
    #maintenance node is about.
    my $numwus = scalar @wu_ids;
    foreach my $wu_id (@wu_ids[1 ..$numwus-1] ) {

      my $wu = getNodeById($wu_id);
      my $wuname = $$wu{'title'};
      my $author = getNodeById($$wu{'author_user'});
      my $authorname = $$author{'title'};

      my $vars = getVars $author;

      $talktome .=  "<tr>".
        "<td>" . linkNode($$author{node_id})."</td>".
        "<td>" . linkNode($$wu{node_id})."</td>".
        "<td>" . $$vars{numwriteups}."</td>".
        "</tr>\n";

#      $$vars{numwriteups}--;

#      setVars($author,$vars);

      #Gotta notify people, let them know what's going on...
      my $msg = "Okay, that didn't work... you might be spammed a
      little with multiple /msgs like this one while I try to fix your
      writeup count due to [$wuname]. Apologies again.";

      $HTMLVARS {msgparams} = {
                               'author_id' => $swappian_id,
                               'recipient_id' => $$author{node_id},
                               'message' => $msg,
                               'ccself' => 1
                               };

#      htmlcode('sendPrivateMessage','');
   }
  }

  $table .= "</table>";
  $talktome .= "</table>";

  $str .= $table."<hr width=\"50%\">".$talktome;
}
return $str;
%]
