node.doctext:
[%
my $angel_id = $query -> param('angel_id');
return "ACK! No angel id!" unless $angel_id;

my $tomb = $DB->sqlSelectHashref('*', 'heaven', "node_id=$angel_id");

return "ACK! No angel with id $angel_id!" unless $tomb;

return "ACK! Angel id $angel_id is not a writeup!"
	unless $tomb->{type_nodetype} == getId(getType('writeup'));

return "ACK! Angel id $angel_id is not one of yours!"
	unless $tomb->{author_user} == $$USER{node_id};

my $N = htmlcode('angelToDraft', $tomb);

if (ref $N){ # $N is the draft
	local $GNODE = $N;
	return "<p>Angel id $angel_id successfully reincarnated:</p>
		<h1>$$N{title}</h1>".
		parseCode(getNode('draft display page', 'htmlpage')->{page});
}

return "Ack! $N"; # $N is an error message
%]
