node.doctext:
[%
  my $str;
  $str.="<maintenance>\n";

  foreach (keys %{$Everything::CONF->system->{maintenance_nodes}})
  {
    my $n = $_;
    $n =~ s/\_/ /g;
    my $node = getNode($n, "e2node");
    $str.="<e2link node_id=\"$$node{node_id}\">$$node{title}</e2link>\n";
  }

  $str.="</maintenance>\n";
  return $str;

%]
